<style lang="scss">
    .carousel {
        margin: 100px auto;
        width: 90%;
        display: flex;
        overflow-x: auto;
        &::-webkit-scrollbar{
            display: none;
        }
        .group {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 1em;
            animation: spin 20s infinite linear;
            animation-delay: 150ms;
            animation-play-state: running;
            padding-right: 1em;
        }
        .card {
            flex: 0 0 5em;
            height: 5em;
            padding: 1em;
            font-size: 1.8rem;
            border-radius: .2em;
            text-align: center;
            align-content: center;
            svg {
                fill: royalblue;
            }
        }
        @keyframes spin {
            from {translate: 0;}
            to {translate: -100%;}
        }
    }
</style>

